select * 
from (select @rownum:=@rownum+1 as rownum, m.* from `hellchang_schema_test`.`myRoutine` as m, 
(select @rownum :=0) R order by date desc)sub 
group by id,title order by sub.rownum asc limit 5;



select * 
from (select @rownum:=@rownum+1 as rownum, m.* from `hellchang_schema_test`.`myRoutine` as m, 
(select @rownum :=0) R order by date desc)sub
where rownum >= #{rownum}
group by id,title order by sub.rownum asc limit 5;
